//    		L_Cmd.CommandText = "SELECT COUNT(SC.column_id) FROM sys.tables ST INNER JOIN sys.columns SC ON ST.object_id = SC.object_id WHERE ST.name = 'SRITU' AND SC.name = 'KEIGEN'";
