            // 日次処理
            else
            {
    			SiwakeTable = "ZDATA";
    		}
    
    		// 仕訳テーブルに軽減税率用のカラムが追加されているかどうかをチェック
    		L_Cmd             = H_Con.CreateCommand();

// 2020/03/06 Ver02.20.01.9901 PostgreSQL対応 --->
