    				throw new MyException(MC_RESULT_ERR_DBREAD, ex);
    			}

    			if (L_Reader.Read())
                {
    				//  部門取引先科目使用フラグ
    				H_3Code[L_i, 0] = L_Reader.GetInt16(0) == 1 ? true : false;
    				//  取引先科目枝番使用フラグ
    				H_3Code[L_i, 1] = L_Reader.GetInt16(1) == 1 ? true : false;
    				//  セグメント取引先科目使用フラグ
