    			if (L_UseZkbn == 11 || L_UseZkbn == 12 || L_UseZkbn == 13 || L_UseZkbn == 14 || L_UseZkbn == 41 || L_UseZkbn == 42)
                {
    				H_JZkbn = L_UseZkbn.ToString();
    				return true;
    			}
                else
                {
    				return false;
    			}    
    		}
            else
            {
    			// レコードの課税区分と使用できる課税区分が一致しなければエラー
    			if (L_ChkJZkbn == L_UseZkbn)
                {
    				return true;
    			}
                else
                {
    				return false;
    			}
    		}
            #endregion
