        private bool ChkExtension(string P_FilePath, int P_Flg1)
    	{    
    		string L_Extension = System.IO.Path.GetExtension(P_FilePath);
    		bool   L_Ret       = false;
    
    		L_Extension = L_Extension.ToUpper();    

// 2021/10/14 Ver02.22.13 令和３年改正電帳法対応 --->
//    		if (P_Flg1 != 3)
            if (P_Flg1 < 3)
// <--- 2021/10/14 Ver02.22.13 令和３年改正電帳法対応
            {
                // 無条件にPDFファイルを追加 2021/03/31
                //// 無条件で使用可能
                //if (L_Extension == ".BMP" || L_Extension == ".JPEG" || L_Extension == ".PNG" || L_Extension == ".JPG")
                if (L_Extension == ".BMP" || L_Extension == ".JPEG" || L_Extension == ".PNG" || L_Extension == ".JPG" || L_Extension == ".PDF")
                {
    				L_Ret = true;
    			}
                else
                {
                    // 当てはまる拡張子がない場合、永久ループなので修正 2021/03/31
                    //*
                    int chk_count = 0;
                    //*
                    do
                    {
                        // 無条件に移動 2021/03/31
                        //// 会社情報設定により使用可能
                        //if (L_Extension == ".PDF")
                        //{
                        //    if (P_PDF == 1)
                        //    {
                        //        L_Ret = true;
                        //        break;
                        //    }
                        //}

                        if (L_Extension == ".XPS")
                        {
                            //* 無限ループ対応 2021/03/31
                            chk_count = 1;
                            //* 無限ループ対応 2021/03/31
                            if (P_XPS == 1)
                            {
                                L_Ret = true;
                                break;
                            }
                        }

                        if (L_Extension == ".OXPS")
                        {
                            //* 無限ループ対応 2021/03/31
                            chk_count = 1;
                            //* 無限ループ対応 2021/03/31
                            if (P_OXPS == 1)
                            {
                                L_Ret = true;
                                break;
                            }
                        }

                        if (L_Extension == ".DOC" || L_Extension == ".DOCX")
                        {
                            //* 無限ループ対応 2021/03/31
                            chk_count = 1;
                            //* 無限ループ対応 2021/03/31
                            if (P_DOCDOCX == 1)
                            {
                                L_Ret = true;
                                break;
                            }
                        }

                        if (L_Extension == ".XLS" || L_Extension == ".XLSX")
                        {
                            //* 無限ループ対応 2021/03/31
                            chk_count = 1;
                            //* 無限ループ対応 2021/03/31
                            if (P_XLSXLSX == 1)
                            {
                                L_Ret = true;
                                break;
                            }
                        }

// 2022/04/11 Ver02.24.01 e文書任意項目＆拡張子MSG対応 --->
                        if (L_Extension == ".MSG")
                        {
                            chk_count = 1;
                            if (P_MSG == 1)
                            {
                                L_Ret = true;
                                break;
                            }
                        }
// <--- 2022/04/11 Ver02.24.01 e文書任意項目＆拡張子MSG対応

                    //* 無限ループ対応 2021/03/31
                    //*} while (true);
                    } while (chk_count < 1);
                    //* 無限ループ対応 2021/03/31
                }
            }
// 2023/12/26 Ver02.28.01 電子取引(TSなし)拡張子制限排除対応 *-
            else if (P_Flg1 == 5)
            {
                L_Ret = true;
            }
// -*
            else
            {
    			// e文書は「PDF」のみ許可
    			if (L_Extension == ".PDF")
                {
    				L_Ret = true;
    			}
    		}
    
    		return L_Ret;
    	}    
    
