            {
    			SetErrLog_SUB_LINK(ref H_ErrLogLink, ref H_ErrCnt, Convert.ToInt32(H_Data[G_Index_LNO]), 2, H_Data[Convert.ToInt32(G_Index_LNAM)], H_Data[Convert.ToInt32(G_Index_LNAM)], MC_ERRLOG_LINK_0);
